    ld [$2000], a  ; Load Bank
    jp [hl]

BankSwitch: ; 054f (0:054f) 
    ld e, a
    ld a, [hLoadedROMBank]  ; currently-loaded Bank
    cp e
    jr z, .doJump
    push af
    ld a, e
