    call .doJump
    pop de
    ld a, d
.loadNewBank
    push hl
    push de
    ld hl, $ffff
    ld d, [hl]
    ld [hl], $0
    ld [$2000], a
    ld [hLoadedROMBank], a
    ld [hl], d
    pop de
    pop hl
    ret
.doJump
