CopyDataToRAM: ; 0666 (0:0666) spooky
; Copies data from any bank to either working RAM or video RAM
; Input: hl = address of data to copy
;        a  = bank of data to copy
;        de = destination for data
;        bc = number of bytes to copy
    bit 7, h
    jr nz, .copyToVideoRAM
    ld [$fffa], a
    ld a, [hLoadedROMBank]
    push af
    ld a, [$fffa]
    ld [hLoadedROMBank], a
    ld [$2000], a
    scf
    jr .copyData
.copyToVideoRAM
    ld [$4000], a
    and a
.copyData
    push af
.copyLoop
    ld a, [hli]
    ld [de], a
    inc de
    dec bc
    ld a, c
    or b
    jr nz, .copyLoop
    pop af
    ret nc
    pop af
    ld [hLoadedROMBank], a
    ld [$2000], a
    ret

