TitlescreenLoop: ; c089 (3:4089) 
    call Func_c0ee
    call HandleTitlescreenAnimations
    ld a, [hNewlyPressedButtons]
    bit BIT_A_BUTTON, a  ; was A button pressed?
    jr z, .AButtonNotPressed
    ld a, [wTitleScreenCursorSelection]
    and a
    jr nz, .asm_c0d3
