    jr z, .noPreviouslySavedGame
    ld de, $0001
    call PlaySoundEffect
    xor a
    ld [$d910], a
    ld a, $2
    ld [$d911], a
    ld a, $1
    ld [wTitleScreenGameStartCursorSelection], a
    ld hl, wScreenState
    inc [hl]
    ret
.noPreviouslySavedGame
    ld de, $0000
    call Func_490
    rst $10
    ld de, $0027
    call PlaySoundEffect
