; Input:  a = billboard picture id
    push hl
    ld c, a
    ld b, $0
    sla c
    add c
    ld c, a
    ld hl, BillboardPicturePointers
    add hl, bc
    ld a, [hli]
    ld c, a
    ld a, [hli]
    ld b, a
    ld a, [hl]
    ld h, b
    ld l, c
    ld bc, $0180  ; get the address of the "off" version of the picture
    add hl, bc
    ld de, $8900
    ld bc, $0180
    call LoadVRAMData
    pop hl
    ret

